'use strict';

var _index = require('./index');

test('getCountry returns expected value', function () {
    var result = (0, _index.getCountry)('UK');
    expect(result.abbr).toBe('UK');
    expect(result.cur).toBe('GBP');
});

//Custom countries
var moreCountries = {
    'Zak': {
        'abbr': 'Zk',
        'cur': 'Zk'
    },
    'Zan': {
        'abbr': 'Zn',
        'cur': 'Zn'
    }
};

test('getCountry with custom country list', function () {
    var result = (0, _index.getCountry)('Zak', moreCountries);
    expect(result.abbr).toBe('Zk');
    expect(result.cur).toBe('Zk');
});

test('getCountry with custom country list, use from the main list', function () {
    var result = (0, _index.getCountry)('UK', moreCountries);
    expect(result.abbr).toBe('UK');
    expect(result.cur).toBe('GBP');
});

test('getCountry returns undefined', function () {
    var result = (0, _index.getCountry)('Unknown Country');
    expect(result).toBe.undefined;
});