'use strict';

var _index = require('../src/index');

test('getCountryByAbbreviation returns expected value', function () {
    var result = (0, _index.getCountryByAbbreviation)('UK');
    expect(result).toEqual('UK');
});

test('getCountryByAbbreviation returns expected value', function () {
    var result = (0, _index.getCountryByAbbreviation)('US');
    expect(result).toEqual('USA');
});

test('getCountryByAbbreviation returns undefined', function () {
    var result = (0, _index.getCountryByAbbreviation)('NONE');
    expect(result).toBe.undefined;
});