'use strict';

var _index = require('../src/index');

test('getCurrency returns expected value', function () {
    var result = (0, _index.getCurrency)('USD');
    expect(result.symbolFormat).toEqual('${#}');
    expect(result.name).toBe('U.S. Dollar (USD)');
});

test('getCurrency returns undefined', function () {
    var result = (0, _index.getCurrency)('NONE');
    expect(result).toBe.undefined;
});