'use strict';

var _index = require('../src/index');

test('getCurrencyAbbreviation returns expected value', function () {
    var result = (0, _index.getCurrencyAbbreviation)('UK');
    expect(result).toEqual('GBP');
});

var otherCountries = {
    'Europe': {
        'abbr': 'europe',
        'cur': 'EUR'
    },
    'Asia': {
        'abbr': 'asia',
        'cur': 'RS'
    }
};

test('getCurrencyAbbreviation returns expected value for additional countries', function () {
    var result = (0, _index.getCurrencyAbbreviation)('Europe', otherCountries);
    expect(result).toEqual('EUR');
});

test('getCurrencyAbbreviation returns undefined', function () {
    var result = (0, _index.getCurrencyAbbreviation)('NONE');
    expect(result).toBe.undefined;
});