'use strict';

var _index = require('../src/index');

test('getCurrencyAbbreviationFromName returns expected value', function () {
    var result = (0, _index.getCurrencyAbbreviationFromName)('British Pound (GBP)');
    expect(result).toEqual('GBP');
});

test('getCurrencyAbbreviation returns undefined', function () {
    var result = (0, _index.getCurrencyAbbreviationFromName)('NONE');
    expect(result).toBe.undefined;
});